function livestatus($state) {
//	$status = array();
	if($state == "green") {
		$status['desc'] = 'fully operational';
		$status['desc_short'] = '@normal';
		$status['color'] = '#3abe14';
		$status['image'] = 'status-green.jpg';
		$status['icon'] = 'status-icon-green.png';
	} else if($state == "orange") {
		$status['desc'] = 'minor service disruption';
		$status['desc_short'] = '@service disruption';
		$status['color'] = '#bd6c14';
		$status['image'] = 'status-orange.jpg';
		$status['icon'] = 'status-icon-orange.png';
	} else if($state == "red") {
		$status['desc'] = 'major service disruption';
		$status['desc_short'] = '@service disruption';
		$status['color'] = '#be2615';
		$status['image'] = 'status-red.jpg';
		$status['icon'] = 'status-icon-red.png';
	} else if($state == "maintenance") {
		$status['desc'] = 'partial services in maintenance';
		$status['desc_short'] = '@in maintenance mode';
		$status['color'] = '#164dc2';
		$status['image'] = 'status-blue.jpg';
		$status['icon'] = 'status-icon-blue.png';
	} else {
		$status['desc'] = 'investigating services';
		$status['desc_short'] = '@investigating';
		$status['color'] = '#7516c4';
		$status['image'] = 'status-violet.jpg';
		$status['icon'] = 'status-icon-violet.png';
	}
	return($status);
}
// ##################################### END OF GLOBAL VARS #################################################

