/* BEGIN FUNCTION - PING2 THE HOST WITH FSOCKOPEN*/
function ping2($host,$port,$timeout=6) {
        $fsock = fsockopen($host, $port, $errno, $errstr, $timeout);
        if ( ! $fsock ) {
                return FALSE;
        } else {
                return TRUE;
        }
}
/* END FUNCTION - PING2 THE HOST WITH FSOCKOPEN*/


