// function to check responsetime for a webbserver
function responseDomain($domain){
    $starttime = microtime(true);
    $file      = fsockopen ($domain, 443, $errno, $errstr, 10);
    $stoptime  = microtime(true);
    $responseTime    = 0;

    if (!$file){
        $responseTime = -1;  // Site is down
    }
    else{
        fclose($file);
        $responseTime = ($stoptime - $starttime) * 25000;
        $responseTime = floor($responseTime);
    }
    return $responseTime;
}

